          displayName
          country {
            name
            code
          }
        }
        mexicoDetails {
          defaultShipFromIsMx
        }
        ossRegistrationCountry {
          code
          name
        }
      }
    }
    sellerVerification {
      hasCompleted
      canStart
      isKycVerification
      kycVerification {
        canStart
      }
    }
  }
  platformConstants {
    euVatCountries {
      code
      name
    }
    tax {
      us {
        nomadStates
        marketplaceMunicipalities {
          stateCode
          cities
          counties
          districts
        }
        marketplaceStates
      }
      ca {
        pstQstProvinces
        marketplaceProvinces
      }
      marketplaceUnions{
        union {
          code
          name
          countries{
            code
          }
        }
        launchDate {
          unix
          hasPassed
          formatted(fmt: "MMMM d, yyyy")
        }
      }
      marketplaceCountries {
        country {
          code
        }
        launchDate {
          unix
        }
