    prePurchaseTicketCount: ticketCount(states: [AWAITING_MERCHANT], types: [PRE_PURCHASE])
    postCSTicketCount: ticketCount(states: [AWAITING_MERCHANT], types: [POST_CUSTOMER_SUPPORT])
